import howFarImg from '../assets/projects/howfar.png';
import bloomImg from '../assets/projects/bloom.png';
import pdfToolImg from '../assets/projects/pdf-tool.png';
import xchangeImg from '../assets/projects/xchange.png';
import multipageImg from '../assets/projects/multipage.png';

const ProjectCard = ({ title, description, features, tech, link, image }) => (
  <div className="bg-white p-6 rounded-xl shadow-md flex flex-col md:flex-row gap-6">
    <img src={image} alt={title} className="w-full md:w-48 h-32 object-cover rounded-md" />
    <div>
      <h3 className="text-xl font-semibold">{title}</h3>
      <p className="mt-2 text-sm">{description}</p>
      {features && (
        <ul className="list-disc list-inside mt-2 text-sm text-gray-700">
          {features.map((f, i) => <li key={i}>{f}</li>)}
        </ul>
      )}
      {tech && <p className="mt-2 text-sm text-gray-600">{tech}</p>}
      {link && (
        <a href={link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline mt-2 inline-block">
          View Project
        </a>
      )}
    </div>
  </div>
);

const Projects = () => (
  <section id="projects" className="p-10 bg-gray-50">
    <h2 className="text-2xl font-bold mb-6">Projects</h2>
    <div className="space-y-8">

      <ProjectCard
        title="How Far"
        description="A React Native app that tracks user movement using sensors."
        features={["Real-time distance", "Pedometer", "Speed calculation"]}
        tech="React Native"
        link="https://github.com/Bensunny0619/How-Far"
        image={howFarImg}
      />

      <ProjectCard
        title="Bloom App"
        description="A productivity mobile app with unified navigation."
        features={["Custom UI", "iOS & Android compatible", "Built with React Native"]}
        tech="React Native, JavaScript"
        link="#"
        image={bloomImg}
      />

      <ProjectCard
        title="PDF Annotation Tool"
        description="Web-based PDF tool with annotation and digital signatures."
        features={["PDF Viewer", "Digital Signatures", "Export with markups"]}
        tech="React, Next.js, Tailwind CSS"
        link="#"
        image={pdfToolImg}
      />

      <ProjectCard
        title="Xchange - Crypto Trading App"
        description="A mobile app for crypto trading with real-time data and secure login."
        features={["Auth", "Wallet Mgmt", "Market Data"]}
        tech="React Native, Expo, Firebase"
        link="#"
        image={xchangeImg}
      />

      <ProjectCard
        title="Responsive Multi-Page Website"
        description="A responsive, multi-page website built for optimal performance."
        tech="HTML, Tailwind CSS, JavaScript"
        link="https://flourishing-kheer-45a9a6.netlify.app/"
        image={multipageImg}
      />

    </div>
  </section>
);

export default Projects;
