// Import all project images
import howFar1 from '../assets/projects/howfar get-started.jpg';
import howFar2 from '../assets/projects/howfar main.jpg';
import bloomImg from '../assets/projects/bloom-Img.png';
import pdfToolImg from '../assets/projects/pdfannote.png';
import xchangeImg from '../assets/projects/xchange.jpg';
import multipageImg from '../assets/projects/web.png';
import rmpw1 from '../assets/projects/da1.png';
import rmpw2 from '../assets/projects/da2.png';
import rmpw3 from '../assets/projects/da3.png';
import rmpw4 from '../assets/projects/da4.png';
import rmpw5 from '../assets/projects/da5.png';
import rmpw6 from '../assets/projects/da6.png';
import rmpw7 from '../assets/projects/da7.png';
import bloomImg2 from '../assets/projects/bloom-home.jpg';
import bloomImg3 from '../assets/projects/bloom workspace.jpg';
import bloomImg4 from '../assets/projects/bloom get-started.jpg';
import pd1 from '../assets/projects/pd1.png';
import pd2 from '../assets/projects/pd2.png';  
import pd3 from '../assets/projects/pd3.png';  
import pd4 from '../assets/projects/pd4.png';  
// Updated ProjectCard component that supports multiple images
const ProjectCard = ({ title, description, features, tech, link, image, images }) => (
  <div className="bg-white p-6 rounded-xl shadow-md">
    <h3 className="text-xl font-semibold">{title}</h3>
    <p className="mt-2 text-sm">{description}</p>

    {features && (
      <ul className="list-disc list-inside mt-2 text-sm text-gray-700">
        {features.map((f, i) => <li key={i}>{f}</li>)}
      </ul>
    )}

    {tech && <p className="mt-2 text-sm text-gray-600">{tech}</p>}

    {link && (
      <a href={link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline mt-2 inline-block">
        View Project
      </a>
    )}

    {/* Support single or multiple images */}
    {images ? (
      <div className="grid grid-cols-2 sm:grid-cols-5 gap-4 mt-4">
        {images.map((img, i) => (
          <img
            key={i}
            src={img}
            alt={`${title} screenshot ${i + 1}`}
            className="rounded-lg shadow w-50 h-50 object-cover" // Adjusted size
          />
        ))}
      </div>
    ) : image ? (
      <img
        src={image}
        alt={title}
        className="w-50 h-50 mt-4 rounded-lg shadow object-cover" // Adjusted size
      />
    ) : null}
  </div>
);

const Projects = () => (
  <section id="projects" className="p-10 bg-gray-50">
    <h2 className="text-2xl font-bold mb-6">Projects</h2>
    <div className="space-y-8">

      <ProjectCard
        title="How Far"
        description="A React Native app that tracks user movement using sensors."
        features={["Real-time distance", "Pedometer", "Speed calculation"]}
        tech="React Native"
        link="https://github.com/Bensunny0619/How-Far"
        images={[howFar1, howFar2]}
      />

      <ProjectCard
        title="Bloom App"
        description="A productivity mobile app with unified navigation."
        features={["Android compatible", "Webview", "Built with React Native"]}
        tech="React Native, JavaScript"
        link="https://github.com/Bensunny0619/BloomWebviewApp"
        images={[bloomImg4, bloomImg3, bloomImg2]}
      />

      <ProjectCard
        title="PDF Annotation Tool"
        description="Web-based PDF tool with annotation and digital signatures."
        features={["PDF Viewer", "Digital Signatures", "Export with markups"]}
        tech="React, Next.js, Tailwind CSS"
        link="https://github.com/Bensunny0619/pdf-annotation"
        images={[pdfToolImg, pd1, pd2, pd3, pd4]}
      />

      <ProjectCard
        title="Xchange - Crypto Trading App"
        description="A mobile app for crypto trading with real-time data and secure login."
        features={["Auth", "Wallet Mgmt", "Market Data"]}
        tech="React Native, Expo, Firebase"
        link="https://github.com/Bensunny0619/Xchange"
        image={xchangeImg}
      />

      <ProjectCard
        title="Responsive Multi-Page Website"
        description="A responsive, multi-page website built for optimal performance."
        tech="HTML, Tailwind CSS, JavaScript"
        link="https://flourishing-kheer-45a9a6.netlify.app/"
        images={[rmpw1, rmpw2, rmpw5, rmpw6, rmpw7]}
      />

    </div>
  </section>
);

export default Projects;
